mapper.select:
select *
from sc_select s
where s.stu_id = #{stuId}
  and s.course_id = #{courseId}

mapper.delete:
delete
from sc_select
where stu_id = #{stuId}
  and course_id = #{courseId}

mapper.update:
update sc_select
set during       = #{during},
    is_necessary = #{isNecessary},
    is_recommend = #{isRecommend},
    select_reason = #{selectReason}
where stu_id = #{stuId}
  and course_id = #{courseId}
