mapper.select:
select *
from sc_selected s
where s.stu_id = #{stuId}
  and s.course_id = #{courseId}

mapper.delete:
delete
from sc_selected
where stu_id = #{stuId}
  and course_id = #{courseId}

mapper.update:
update sc_selected
set during = #{during},
    score = #{score},
    evaluation = #{evaluation},
    is_qualified = #{isQualified}
where stu_id = #{stuId}
    and course_id = #{courseId}
