mapper.select:
select *
from st_evaluation s
where s.stu_id = #{stuId}
  and s.teacher_id = #{teacherId}

mapper.delete:
delete
from st_evaluation
where stu_id = #{stuId}
  and teacher_id = #{teacherId}

mapper.update:
update st_evaluation
set during     = #{during},
    rank       = #{rank},
    evaluation = #{evaluation}
where stu_id = #{stuId}
  and teacher_id = #{teacherId}
