mapper.select:
select *
from tc_difficulty t
where t.teacher_id = #{teacherId}
  and t.course_id = #{courseId}

mapper.delete:
delete
from tc_difficulty
where teacher_id = #{teacherId}
  and course_id = #{courseId}

mapper.update:
update tc_difficulty
set score  = #{score},
    advice = #{advice}
where teacher_id = #{teacherId}
  and course_id = #{courseId}
